sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
    var incidentGR = new GlideRecord('incident');
	incidentGR.initialize();
	var updates = current.sys_mod_count;	
		 if(updates != 0){
			 template.print('<span style="font-family:verdana;">UPDATED.</span>');

		 }
		 else{
			 template.print('<span style="font-family:verdana;">CREATED.</span>');
		 }
		template.print('<span style="font-family:verdana;"><a href="https://dev21654.service-now.com/nav_to.do?uri=%2Fincident_list.do%3Fsysparm_userpref_module%3D4fed4395c0a8016400fcf06c27b1e6c6%26sysparm_query%3Dactive%3Dtrue%5EEQ%26active%3Dtrue%26sysparm_clear_stack%3Dtrue"> CLICK HERE </a>to view the INCIDENT table.</span>');

})(current, template, email, email_action, event);
