sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var notificationGR = new GlideRecord('x_197131_sensiple_notification_keywords');
	notificationGR.query();
	var temp=0;

	var shortDescription = current.getValue('description');
	//var description = current.getValue('description');
	var incidentNumber = current.getValue('number');
	var lwc_srtDisp =  shortDescription.toLowerCase();
	var splitedDiscription = lwc_srtDisp.split(/[ ,]+/);

	while(notificationGR.next()){
		var keywords = notificationGR.getValue('keywords');	
		var lwc_kyWrds = keywords.toLowerCase();
		var words = lwc_kyWrds.split(/[ ,]+/);
		for(var i = 0; i < words.length; i++){
			for(var j = 0;  j < splitedDiscription.length; j++){
				if(words[i]==splitedDiscription[j]){					
					temp=1;
				}
			}
		}
	}

	var notificationGR1 = new GlideRecord('x_197131_sensiple_notification_keywords');
	notificationGR1.query();
		while(notificationGR1.next()){
			if(temp!=0){

			gs.eventQueue('x_197131_sensiple.sensiple.ticket.regist',
			current,notificationGR1.getValue('email_address1'),incidentNumber );
				if(notificationGR1.getValue('email_address2')){
					gs.eventQueue('x_197131_sensiple.sensiple.ticket.regist',
			current,notificationGR1.getValue('email_address2'),incidentNumber );
				}
				if(notificationGR1.getValue('email_address3')){
					gs.eventQueue('x_197131_sensiple.sensiple.ticket.regist',
			current,notificationGR1.getValue('email_address3'),incidentNumber );
				}

		}	
	}

})(current, previous);
