sys_script_include.script:
var PriorityValidation = Class.create();
PriorityValidation.prototype =  Object.extendsObject(global.AbstractAjaxProcessor, {
	getFields: function () {

		var priority = this.getParameter('priority');
		var keywords = this.getParameter('keywords');
		var email_address1 = this.getParameter('email_address1');
		var email_address2 = this.getParameter('email_address2');
		var email_address3 = this.getParameter('email_address3');

		var priorityGR = new GlideRecord('x_197131_sensiple_notification_keywords');
		priorityGR.addQuery('priority', '=', priority);
		priorityGR.query();


		//var result = this.newItem("result");
		//result.setAttribute("message","returning all favorites");
		if (priorityGR.next()) {

			return false; 
		}
		else{
			var priorityGRinsert1 = new GlideRecord('x_197131_sensiple_notification_keywords');
			priorityGRinsert1.keywords = keywords;
			priorityGRinsert1.email_address1 = email_address1;
			priorityGRinsert1.email_address2 = email_address2;
			priorityGRinsert1.email_address3 = email_address3;
			priorityGRinsert1.priority = priority;
			priorityGRinsert1.insert();
			return true;
		}

    },

    type: 'PriorityValidation'
});
