sys_ui_action.script:
var URL = "https://dev21654.service-now.com/nav_to.do?uri=%2Fx_197131_sensiple_notification_keywords_list.do%3Fsysparm_clear_stack%3Dtrue";	

var keywords = current.getValue('keywords');
var email_address1 = current.getValue('email_address1');
var email_address2 =  current.getValue('email_address2');
var email_address3 = current.getValue('email_address3');
var priority = current.getValue('priority');

var priorityGR = new GlideRecord('x_197131_sensiple_notification_keywords');
	priorityGR.addQuery('priority', '=', priority);
	priorityGR.query();

if (priorityGR.next()) {

		gs.addInfoMessage("Selected priority alraedy contain records, you can not insert new one. Please update the existing one.");
	//action.setRedirectURL(current);
	}
else{
		var priorityGRinsert1 = new GlideRecord('x_197131_sensiple_notification_keywords');
		priorityGRinsert1.keywords = keywords;
		priorityGRinsert1.email_address1 = email_address1;
		priorityGRinsert1.email_address2 = email_address2;
		priorityGRinsert1.email_address3 = email_address3;
		priorityGRinsert1.priority = priority;
		priorityGRinsert1.insert();

	}
