sys_ui_action.script:
    var keywords = current.getValue('keywords');
	var email_address1 = current.getValue('email_address1');
	var email_address2 =  current.getValue('email_address2');
	var email_address3 = current.getValue('email_address3');
	var priority = current.getValue('priority');
	var colourCode = current.getValue('cpu_alert_colour');
	var priorityInTable = "";

	var priorityGR = new GlideRecord('x_197131_sensiple_notification_keywords');
		priorityGR.addQuery('priority', '=', priority);
		priorityGR.query();	

	if(current.isNewRecord()){
	gs.addInfoMessage('New');
		if (priorityGR.next()) {			
			current.priority.setError("Selected priority already contain records, you can not insert new one. Please update the existing one.");		
		current.setAbortAction(true);
		}else{			
			current.insert();			
		}
	}else{
		gs.addInfoMessage('update');
			var priorityGR1 = new GlideRecord('x_197131_sensiple_notification_keywords');
			priorityGR1.addQuery('priority', '=', priority);
			priorityGR1.query();			
			if (priorityGR1.next()) {
				current.priority.setError("Selected priority already contain records, you can not insert new one. Please update the existing one.");		
				current.setAbortAction(true);				
			}
			else{				
				current.update();				
			}

		}
