sys_ui_action.script:
//function onChange(control, oldValue, newValue, isLoading, isTemplate) {
function onSubmit() {
	var URL = "https://dev21654.service-now.com/nav_to.do?uri=%2Fx_197131_sensiple_notification_keywords_list.do%3Fsysparm_clear_stack%3Dtrue";	
	var emailRegex = "/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/";
	var keywords = g_form.getValue('keywords');
	var email_address1 = g_form.getValue('email_address1');
	var email_address2 =  g_form.getValue('email_address2');
	var email_address3 = g_form.getValue('email_address3');
	var priority = g_form.getValue('priority');
	var ga = new GlideAjax('PriorityValidation');

	//var flagKeywords;
	var flagEmail_1 = false;
	var flagEmail_2 = false;
	var flagEmail_3 = false;

	if(keywords!="" && priority!="" && emailRegex.test(email_address1)){
		flagEmail_1 = true;
	}
	else if(keywords!="" && priority!="" && emailRegex.test(email_address1) && emailRegex.test(email_address2)){
		flagEmail_2 = true;
	}
	else if(keywords!="" && priority!="" && emailRegex.test(email_address1) && emailRegex.test(email_address2)){
		flagEmail_3 = true;
	}

	if(flagEmail_1==true||flagEmail_2==true||flagEmail_3==true){

		ga.addParam("sysparm_name", "getFields"); 
		ga.addParam("priority", priority);
		ga.addParam("keywords", keywords);
		ga.addParam("email_address1", email_address1);
		ga.addParam("email_address2", email_address2);
		ga.addParam("email_address3", email_address3);
		ga.getXML(GetResponse); 			
	}else{
		g_form.showFieldMsg("keywords", "KeyWord Field should not be empty", "error");
		g_form.showFieldMsg("email_address1", "Email address1 is mandatory ", "error");
		g_form.showFieldMsg("priority", "Select the priority", "error");		
	}

	function GetResponse(response) {
	  var answer = response.responseXML.documentElement.getAttribute("answer");

		if (answer == "false"){
			//alert('if');
			//g_form.showFieldMsg("priority", "Hello World", "error");
			confirm("Selected priority alraedy contain records, you can not insert new one. Please update the existing one.");
			top.window.location = URL;
		}		
		else{		
			alert('else');
			confirm("Data inserted successfully!");
			top.window.location = URL;
		}
	}

}
